import React from 'react';

import { selectCategory } from '../actions';

import { connect } from 'react-redux';


function onFormSubmit(event, props) {
    event.preventDefault();

    props.selectCategory(props.SelectedCategory, this.state.value)
}

const SearchBox = (props) => {

    //constructor(props) {
    //    super(props);

    //    this.state = { value: '' };
    //}

    return (
        <form onSubmit={(event) => onFormSubmit(event, this.props)}>
            <div className="ui fluid icon input">
                <input type="text" placeholder="Search..." value={this.state.value} onChange={(e) => { this.setState({ value: e.target.value}); }} />
                <button className="ui button" onClick={() => { console.log('Search Clicked', this.state.value); }}>Search</button>
            </div>
        </form>
        );
    
}

const mapStateToProps = (state) => {
    console.log("From SearchBox", state);
    return (
        {
            SelectedCategory: state.categorySelected.category
        });
}

export default connect(mapStateToProps, { selectCategory })(SearchBox);
