import React from 'react';

import { connect } from 'react-redux';

class ResultsDetail extends React.Component {

    render() {
        return (
            <div>
                <textarea readOnly={true} style={{ minWidth: 340 }} />
			</div>
        );

    }
}

const mapStateToProps = (state) => {
    return (
        {
            selectedCategory: state.categorySelected.category,
            filteredSubCategories: state.categorySelected.filteredSubCategories
        });
}

export default connect(mapStateToProps)(ResultsDetail);
