import React from 'react';

import { selectCategory } from '../actions';

import { connect } from 'react-redux';


class SearchBox extends React.Component {

    constructor(props) {
        super(props);

        this.state = { value: '' };
    }

    onFormSubmit(event) {
        event.preventDefault();

        this.props.selectCategory(this.props.SelectedCategory, this.state.value)
    }

    render() {
        return (
            <div className="ui fluid icon input">
                <form onSubmit={this.onFormSubmit}>
                    <input type="text" placeholder="Search..." value={this.state.value} onChange={(e) => { this.setState({ value: e.target.value}); }} />
                    <div className="ui button" onClick={() => { console.log('Search Clicked', this.state.value); }}>Search</div>
                </form>
            </div>
            );
    }
}

const mapStateToProps = (state) => {
    console.log("From SearchBox", state);
    return (
        {
            SelectedCategory: state.categorySelected.category
        });
}

export default connect(mapStateToProps, { selectCategory })(SearchBox);
