import React from 'react';
import FilterButton from './FilterButton';
import SearchBox from './SearchBox';
import ResultsBox from './Results';

import { filterResultCategories } from '../actions';

import { connect } from 'react-redux';

const App = () => {
    return (
        <div className="ui container">
            <h1 className="ui center aligned header">
                STAR WARS
            </h1>
            <div className="ui segment">
                <div className="ui grid">
                    <div className="five wide column">
                        <FilterButton text="PLANETS" onClick={() => this.props.filterResultCategories}/>
                    </div>
                    <div className="five wide column">
                        <FilterButton text="SPACESHIPS" />
                    </div>
                    <div className="five wide column">
                        <FilterButton text="VEHICLES" />
                    </div>
                    <div className="five wide column">
                        <FilterButton text="PEOPLE" />
                    </div>
                    <div className="five wide column">
                        <FilterButton text="FILMS" />
                    </div>
                    <div className="five wide column">
                        <FilterButton text="SPECIES" />
                    </div>
                </div>
            </div>
            <div className="ui segment">
                <div>
                    <SearchBox />
                </div>
            </div>
            <div className="ui segment">
                <ResultsBox />
            </div>
        </div>);
}

const mapStateToProps = (state) => {
    return (
        {
            filteredResults: state.payload
        });
}

export default connect(mapStateToProps, filterResultCategories)(App);
