import React from 'react';

import { selectCategory } from '../actions';

import { connect } from 'react-redux';


class SearchBox extends React.Component {

    static defaultProps = { inputValue:''}

    render() {
        return (
            <div className="ui fluid icon input">
                <input type="text" placeholder="Search..." onChange={(e) => { console.log(e.target.value); a = e.target.value; }} value={a}/>
                <div className="ui button" onClick={() => { console.log('Search Clicked', this.props.inputValue);  }}>Search</div>
            </div>
            );
    }
}

const mapStateToProps = (state) => {
    console.log("From SearchBox", state);
    return (
        {
            SelectedCategory: state.categorySelected.categorySelected
        });
}

export default connect(mapStateToProps, { selectCategory })(SearchBox);
