import { combineReducers } from 'redux';

export default combineReducers({
    StateSelected
});

const stateSelectedReducer = (selectedState = null, action) => {
    if (state.type === 'RESULT_CATEGORIES_FILTERED') {
        return action.payload;
    }

    return selectedState;
}
