import { combineReducers } from 'redux';

export default combineReducers({
    categorySelected: categorySelectedReducer,
    subCategorySelected: subCategorySelectedReducer
});

const categorySelectedReducer = (selectedCategory = null, action) => {
    if (state.type === 'CATEGORY_SELECTED') {
        return action.payload;
    }

    return selectedCategory;
}

const filterSubCategories = (selectedCategory = null, action) => {
    if (action.type === 'FILTER_SUBCATEGORY') {
        return action.payload;
    }

    return selectedCategory;
}

const subCategorySelectedReducer = (selectedSubCategory = null, action) => {
    if (state.type === 'SUBCATEGORY_SELECTED') {
        return action.payload;
    }

    return selectedSubCategory;
}
