import SWAPI from '../apis/SWAPI';

export const selectCategory = (category = '') => {
    console.log('selectCategory Action Hit!', category);

    return async (dispatch) => { 

    var response = [];

    if (category) {
        response = await SWAPI.get(`/${category}`);
        }

        dispatch({
            type: 'CATEGORY_SELECTED',
            payload: {
                category: category,
                filteredSubCategories: response
            }
        });
    };
}

export const filterSubcategory = (category = '') => {
    const response = SWAPI.get('/' + { category });

    return {
        type: 'FILTER_SUBCATEGORY',
        payload: response
    };
}

export const selectSubCategory = (category = '', subCategory = '') => {
    const response = SWAPI.get('/' + { category } + '/' + { subCategory} );

    return {
        type: 'SUBCATEGORY_SELECTED',
        payload: response
    };
}


export const FilterResultsDetails = (searchTerm = '') => {
    console.log('FilterResultsDetails', searchTerm);

    return (
        {
            type: 'RESULT_DETAIL_FILTERED',
            payload: {
                        "climate": "Arid",
                        "diameter": "10465",
                        "gravity": "1 standard",
                        "name": "Tatooine",
                        "orbital_period": "304",
                        "population": "200000",
                        "residents": [
                        "https://swapi.co/api/people/1/",
                        "https://swapi.co/api/people/2/"
                        ]
                }
        });
}
