import React from 'react';
import FilterButton from './FilterButton';

import { selectCategory } from '../actions';

import { connect } from 'react-redux';

class FilteredResults extends React.Component {

    renderList = (filteredSubCategories = [])  => {
        return (
            filteredSubCategories.map((cat) => { return (
                <div>
                    <FilterButton text={cat.name} key={cat.name}/>
                </div>
                )
            })
            );
    }

    render() {
        return (
            <div>
                {this.renderList()}
			</div>
			);

    }
}

const mapStateToProps = (state) => {
    return (
        {
            selectedCategory: state.categorySelected
        });
}

export default connect(mapStateToProps, { selectCategory})(FilteredResults);
