import React from 'react';
import FilterButton from './FilterButton';

import { selectCategory } from '../actions';

import { connect } from 'react-redux';

class FilteredResults extends React.Component {

    renderList = () => {
        console.log(this.props.filteredSubCategories);

        var subCats = this.props.filteredSubCategories;

        if (subCats === 'undefined') {
            subCats = [];
        }

        subCats.map((cat) => {
            return (
                <div>
                    <FilterButton text={cat.name} key={cat.name}/>
                </div>
                )
            });
    }

    render() {
        console.log(this.props);
        return (
            <div>
                {this.renderList()}
			</div>
			);

    }
}

const mapStateToProps = (state) => {
    return (
        {
            selectedCategory: state.categorySelected.category,
            filteredSubCategories: state.categorySelected.filteredSubCategories
        });
}

export default connect(mapStateToProps, { selectCategory})(FilteredResults);
